// Original-Response aus dem Input-Node
const response = $input.first().json.text;

// Validierung: Sicherstellen, dass response ein String ist
if (typeof response !== 'string') {
  throw new Error("Response ist nicht definiert oder kein String.");
}

// Regex zur Extraktion der Parameter und Werte
const regexParams = /\b([a-zA-Z_]+(?:\s*\(.*?\))?):\s*([\d.-]+)\s*(?:\*\s*[a-zA-Z_()]+)?/g;

// Alle Ãœbereinstimmungen fÃ¼r Parameter finden
const matchesParams = [...response.matchAll(regexParams)];

// Initialisiere ein Objekt zur Speicherung der Parameter
const parameters = {};

// Parameterwerte aus Matches extrahieren
matchesParams.forEach(match => {
  // Extrahiere den Parameter-Namen und den Wert
  const paramName = match[1].trim();
  const paramValue = parseFloat(match[2]);

  // Speichere die Parameter im Objekt
  parameters[paramName] = paramValue;
});

// RÃ¼ckgabe des Parameters-Objekts
return {
  json: parameters
};
