// Hole den Input-Daten (angenommen, der Input ist im Format, wie du es beschrieben hast)
const input = $input.first().json.text;

// Teile den Text in einzelne Zeilen auf
const lines = input.split('\n');

// Erstelle ein neues Array mit den extrahierten Zahlen
const numbers = lines.map(line => {
    return { json: { number: parseInt(line.trim(), 10) } };
});

// Rückgabe der neuen Items
return numbers;

// Original-Response aus dem Input-Node
