// Validierung: Sicherstellen, dass response ein String ist
if (typeof response !== 'string') {
  throw new Error("Response ist nicht definiert oder kein String.");
}

// Regex zur Extraktion der neuen Parameter und Werte, jetzt auch mit \n-Trennzeichen
const regexParams = /dt:\s*([\d.]+)\s*\n*|simtime:\s*([\d.]+)\s*\n*|delay:\s*([\d.]+)\s*\n*|g:\s*([\d.]+)\s*\n*|eta:\s*([\d.]+)\s*\n*|epsilon:\s*([\d.]+)\s*\n*|order:\s*([\d]+)\s*\n*|N_rec:\s*([\d]+)/g;

// Alle Übereinstimmungen für Parameter finden
const matchesParams = [...response.matchAll(regexParams)];

// Initialisiere ein Objekt zur Speicherung der Parameter
const parameters = {};

// Parameterwerte aus Matches extrahieren
matchesParams.forEach(match => {
  if (match[1]) parameters.dt = parseFloat(match[1]);
  if (match[2]) parameters.simtime = parseFloat(match[2]);
  if (match[3]) parameters.delay = parseFloat(match[3]);
  if (match[4]) parameters.g = parseFloat(match[4]);
  if (match[5]) parameters.eta = parseFloat(match[5]);
  if (match[6]) parameters.epsilon = parseFloat(match[6]);
  if (match[7]) parameters.order = parseInt(match[7], 10);
  if (match[8]) parameters.N_rec = parseInt(match[8], 10);
});

// Das Ergebnis in das `items` Array schreiben (n8n-konforme Rückgabe)
return [{ json: parameters }];

