    { json: { we: weValue, wi: wiValue } }
];

// Funktion, um alle Inhalte zusammenzuführen
const allContent = items.reduce((acc, item) => {
  // Durchlaufe jedes Element im 'data' Array und füge den 'content' zusammen
  if (item.json.data && Array.isArray(item.json.data)) {
    item.json.data.forEach(dataItem => {
      if (dataItem.content) {
        acc += '\n' + dataItem.content; // Füge den Text hinzu, mit einem Zeilenumbruch
      }
    });
  }
  return acc;
}, ''); // Startwert ist ein leerer String

// Erstelle das neue Item mit dem zusammengefügten Text
