// Regex zur Extraktion der neuen Parameter und Werte, jetzt auch mit \n-Trennzeichen
const regexParams = /area:\s*([\d.]+)\s*\n*|Cm:\s*([\d.]+)\s*\n*|gl:\s*([\d.]+)\s*\n*|El:\s*([\d.-]+)\s*\n*|EK:\s*([\d.-]+)\s*\n*|ENa:\s*([\d.-]+)\s*\n*|g_na:\s*([\d.-]+)\s*\n*|g_kd:\s*([\d.-]+)\s*\n*|VT:\s*([\d.-]+)\s*\n*|taue:\s*([\d.-]+)\s*\n*|taui:\s*([\d.-]+)\s*\n*|Ee:\s*([\d.-]+)\s*\n*|Ei:\s*([\d.-]+)\s*\n*|we:\s*([\d.-]+)\s*\n*|wi:\s*([\d.-]+)\s*\n*/g;

// Regex zur Extraktion der Gehirnregionen
const regexRegions = /Gehirnareal:\s*(.*)/;

// Regex zur Extraktion des stimulus_weight Parameters (mit Werten wie 11, 49)
const regexStimulusWeight = /stimulus_weight:\s*([\d, ]+)/;

// Alle Übereinstimmungen für Parameter finden
const matchesParams = [...response.matchAll(regexParams)];

// Gehirnregionen extrahieren
const brainRegionMatch = response.match(regexRegions);

// stimulus_weight extrahieren
const stimulusWeightMatch = response.match(regexStimulusWeight);

// Initialisiere ein Objekt zur Speicherung der Parameter
const parameters = {};

// Parameterwerte aus Matches extrahieren
matchesParams.forEach(match => {
  if (match[1]) parameters.area = parseFloat(match[1]);
  if (match[2]) parameters.Cm = parseFloat(match[2]);
  if (match[3]) parameters.gl = parseFloat(match[3]);
  if (match[4]) parameters.El = parseFloat(match[4]);
  if (match[5]) parameters.EK = parseFloat(match[5]);
  if (match[6]) parameters.ENa = parseFloat(match[6]);
  if (match[7]) parameters.g_na = parseFloat(match[7]);
  if (match[8]) parameters.g_kd = parseFloat(match[8]);
  if (match[9]) parameters.VT = parseFloat(match[9]);
  if (match[10]) parameters.taue = parseFloat(match[10]);
  if (match[11]) parameters.taui = parseFloat(match[11]);
  if (match[12]) parameters.Ee = parseFloat(match[12]);
  if (match[13]) parameters.Ei = parseFloat(match[13]);
  if (match[14]) parameters.we = parseFloat(match[14]);
  if (match[15]) parameters.wi = parseFloat(match[15]);
});

// stimulus_weight verarbeiten, falls vorhanden
if (stimulusWeightMatch && stimulusWeightMatch[1]) {
  const stimulusWeight = stimulusWeightMatch[1]
    .split(',')  // Trennt die Werte bei Kommas
    .map(val => parseFloat(val.trim())); // Entfernt Leerzeichen und wandelt die Werte in Zahlen um
  parameters.stimulus_weight = stimulusWeight;
}

// Gehirnregionen verarbeiten und als Array speichern, wenn sie gefunden werden
if (brainRegionMatch && brainRegionMatch[1]) {
  const regions = brainRegionMatch[1]
    .split(';') // Trennt die Regionen bei Semikolon
    .map(region => region.trim()); // Entfernt zusätzliche Leerzeichen
  parameters.brainRegions = regions;
}

// Rückgabe des Parameters-Objekts
return {
  json: parameters
};

