const we = $input.first().json.we;  // String mit mathematischem Ausdruck
const wi = $input.first().json.wi;  // String mit mathematischem Ausdruck

function evaluateExpression(expression) {
    try {
        return eval(expression);  // Berechnet den Ausdruck
    } catch (error) {
        throw new Error(`Fehler beim Berechnen des Ausdrucks: ${expression}`);
    }
}

// Berechnung der Werte
const weValue = evaluateExpression(we);
const wiValue = evaluateExpression(wi);

// Beide Werte als ein Item zurückgeben
return [
    { json: { we: weValue, wi: wiValue } }
];

const inputData = $input.all().map(i => i.json); // Holt alle Eingaben als Array von Objekten

if (!Array.isArray(inputData) || inputData.length === 0) {
    throw new Error("inputData ist nicht definiert oder leer");
}

const baseURL = "https://pdas-ooo-wishes-lance.trycloudflare.com/";

const outputUrls = inputData
    .filter(item => item.fileName) // Nur Objekte mit fileName behalten
    .map(item => {
        const fileName = item.fileName.split('/').pop().split('\\').pop(); // Extrahiere den Dateinamen
        return `${baseURL}${fileName}`;
    })
    .join(','); // Komma-separierte Liste

return [{ outputUrls }];
